import React, { Component } from "react";

class App extends Component {
  state = {
    likes: 0,
  };

  addHandler = () => {
    this.setState((this.state.likes += 1));
  };

  render() {
    return (
      <div>
        <h1>Total Likes: {this.state.likes}</h1>
        <button onClick={this.addHandler}>Add One</button>
        <button>Remove One</button>
        <button>Reset</button>
      </div>
    );
  }
}

export default App;
