import React, { Component } from "react";
import "./App.css";

class App extends Component {
  state = {
    likes: 0,
  };

  addHandler = () => {
    this.setState({
      likes: this.state.likes + 1,
    });
  };

  removeHandler = () => {
    if (this.state.likes > 0) {
      this.setState({
        likes: this.state.likes - 1,
      });
    }
  };

  resetHandler = () => {
    this.setState({
      likes: 0,
    });
  };

  changeColor = () => {
    let color = document.getElementsByClassName("circle");
    if (color.style.display === "none") {
      color.style.display === "block";
    } else {
      color.style.display === "none";
    }
  };

  render() {
    return (
      <div class="container">
        <div class="circle"></div>
        <h1>Total Likes: {this.state.likes}</h1>
        <div class="buttons">
          <button onClick={(this.addHandler, this.changeColor)}>Add One</button>
          <button onClick={this.removeHandler}>Remove One</button>
          <button onClick={this.resetHandler}>Reset</button>
        </div>
      </div>
    );
  }
}

export default App;
